function ARGO() {
  const uiObserver = useMemo(() => new Observer(), []);

  return (
    <ARScene
      control={true}
      uiObserver={uiObserver}
      dashboard={
        <>
          <div className='emit-btn-list'>
            {btnList.map(({ type, svgNode }, index) => {
              return (
                <EmitButton
                  key={index}
                  uiObserver={uiObserver}
                  type={type}
                  svgNode={svgNode}
                />
              );
            })}
          </div>
        </>
      }
      landing={
        <>
          <Landing />
        </>
      }
    >
      <ARHitTest />
      {/* <DepthScreen /> */}
      <group>
        {new Array(1).fill(0).map((_, index, items) => {
          return (
            <mesh
              key={index}
              geometry={new SphereBufferGeometry(1.5, 32, 32)}
              material={
                new MeshStandardMaterial({
                  roughness: 0.2,
                  metalness: 1,
                })
              }
              position={{
                x: 0,
                y: 3 * 0.6 - (4 - 1) * 0.3,
                z: -9,
              }}
            ></mesh>
          );
        })}
      </group>
    </ARScene>
  );
}

export { ARGO };
